mapper.update:
update tuser
set
lo = #{lo},
la = #{la}
where userId = #{userID}

mapper.select:
select  userID as USERID,lo as LO,la as LA,tel as PHONE,userName as USERNAME
from tuser
where tel= #{userTel}

mapper.update:
update tuser
set password = #{new_password}
where userId = #{userID}

mapper.select:
SELECT tu.userId as USERID,tu.lo as LO,tu.la as LA,tu.tel as PHONE,tu.userName as USERNAME,tu.userType AS USERTYPE,tu.CID as CID, tc.type AS CARTYPE,
ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000)/1000 as DISTANCE
FROM tuser as tu inner join tcar as tc on tu.tel=tc.driverTel where ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000)/1000 <=#{kmNumber} AND tu.userType=1 AND tc.type=#{carType}

SELECT tu.userid as USERID,tu.lo as LO,tu.la as LA,tu.tel as PHONE,tu.userName as USERNAME,tu.userType AS USERTYPE,tu.CID as CID, tc.type AS CARTYPE,
(ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000))/1000 as DISTANCE
FROM tuser as tu INNER JOIN tcar as tc on tu.tel=tc.driverTel where tel=#{phone} AND tu.userType=1;

SELECT userID as USERID,lo as LO,la as LA,tel as PHONE,userName as USERNAME,tu.userType AS USERTYPE,tu.CID as CID,
(ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000))/1000 as DISTANCE
FROM tuser as tu where tel=#{phone} AND tu.userType=0;

SELECT userID as USERID,lo as LO,la as LA,tel as PHONE,userName as USERNAME,tu.userType AS USERTYPE,tu.CID as CID,
ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000)/1000 as DISTANCE
FROM tuser as tu where ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000)/1000 <=#{kmNumber};

SELECT userID as USERID,lo as LO,la as LA,tel as PHONE,userName as USERNAME,tu.userType AS USERTYPE,tu.CID as CID,
(ROUND(6378.138*2*ASIN(SQRT(POW(SIN( (tu.lo*PI()/180-#{lo}*PI()/180)/2),2) +COS(tu.lo*PI()/180)*COS(#{lo}*PI()/180)* POW(SIN( (tu.la*PI()/180-#{la}*PI()/180)/2),2)))*1000))/1000 as DISTANCE
FROM tuser as tu where tel=#{phone};
